void settings_animation_play(ssd1306_handle_t handle, int delay_ms)
{
    int frame = 0;
    int frame_delay = 100;
    int total_frames = delay_ms / frame_delay;

    for (int i = 0; i < total_frames; i++)
    {
        ssd1306_clear_screen(handle, 0x00);
        ssd1306_draw_bitmap(handle, 40, 8, settings_frames[frame], 48, 48);
        ssd1306_refresh_gram(handle);
        frame = (frame + 1) % (sizeof(settings_frames) / sizeof(settings_frames[0]));
        vTaskDelay(pdMS_TO_TICKS(frame_delay));
    }
}
